//import axios from 'axios';
import React, { useState } from 'react';
import NavBar from "../NavBar";
import axios from 'axios';



export default function SignUp() {
    //Muuttujat, joihin tallennetaan username ja password sekä message, joka tulostuu, kun nappia painetaan
    var [username, setUsername] = useState("");
    var [password, setPassword] = useState("");
    var [message, setMessage] = useState("");

    //Kun painetaan rekisteröidy niin lähetetään post
    let handleSubmit = async (e) => {
        e.preventDefault();
        try {
            //POST palvelimelle jotta saadaan käyttäjätunnukset tietokantaan.
            let res = await fetch("http://localhost:3001/users", {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    username: username,
                    password: password,
                })
            });
            //Ottaa vastauksen ja esittää sen konsolissa.
            let resJson = await res.json();
            console.log(resJson);

            if(username){
                axios.get('/user/get')
                .then(res=>{
                    res.data.forEach(row=>{
                        if(row.username === username){
                            alert("username Allready exits");
                            setMessage("username already exits");
                        }
                    });
                }).catch(err=>{
                    console.log(err);
                })
            }


            if (res.status === 200) {
                setUsername("");
                setPassword("");
                setMessage("User created successfully");
            } else {
                setMessage("Some error occured");
            }
        } catch (err) {
            console.log(err);
        }
        
    };




    //Palautetaan form jossa on signup ikkuna
    return (
        <>
            <NavBar />
            <div className="signup">
                <form onSubmit={handleSubmit}>
                    <div>
                        <input type="text" value={username} placeholder="Username" onChange={(e) => setUsername(e.target.value)} />

                        
                    </div>
                    <div>
                        <input type="password" value={password} placeholder="Password" onChange={(e) => setPassword(e.target.value)} />
                    </div>
                    <button>Create user</button>
                    <div className="message">
                        {message ? <p>{message}</p> : null}
                    </div>
                </form>
            </div>
        </>
    )
}
